import 'regenerator-runtime/runtime'
import React, { useState } from "react";
import { TextField, Button, LinearProgress, Typography } from "@mui/material";
import axios from "axios";
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

function EmotionAnalyzer() {
    const [text, setText] = useState("");
    const [emotionScore, setEmotionScore] = useState(null);
    const [emotionLabel, setEmotionLabel] = useState("Tốt");
    const [loading, setLoading] = useState(false);

    const handleTextChange = (e) => {
        setText(e.target.value);
    };

    const handleSubmit = async () => {
        try {
            setLoading(true);
            const response = await axios.post("/api/diagnose", {text});
            const {score, label} = response.data.data;
            const emotionLabel = +label === 0 ? "Tệ" : "Tốt"
            setEmotionLabel(emotionLabel)
            setEmotionScore(score);
        } catch (error) {
            console.error("Error submitting text:", error);
        } finally {
            setLoading(false);
        }
    };


    const { transcript, listening, resetTranscript } = useSpeechRecognition({language: 'vi-VN'});

    React.useEffect(() => {
        setText(transcript);
    }, [transcript]);

    const startListening = function () {
        resetTranscript();
        SpeechRecognition.startListening({continuous: true})
    };

    const supportedLanguages = SpeechRecognition.getSupportedLanguages;
    const isSupportVNs = supportedLanguages.includes('vi-VN');
    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <Typography variant="h5">Hãy mô tả cảm xúc của bạn</Typography>
            <TextField
                multiline
                rows={4}
                variant="outlined"
                fullWidth
                margin="normal"
                onChange={handleTextChange}
                value={text}
            />
            <Button variant="contained" onClick={listening ? SpeechRecognition.stopListening : startListening}>
                {listening ? 'Stop Listening' : 'Start Listening'}
            </Button>
            <Button variant="contained" onClick={handleSubmit} disabled={loading || isSupportVNs}>
                {loading ? "Đang xử lý..." : "Submit"}
            </Button>
            {emotionScore !== null && (
                <div style={{ marginTop: "20px" }}>
                    <Typography variant="h6">Mức độ cảm xúc: <br/> {emotionLabel} - {emotionScore * 100}%</Typography>
                    <LinearProgress
                        variant="determinate"
                        value={emotionScore * 100}
                        sx={{ marginTop: "10px", height: "10px" }}
                    />
                </div>
            )}
        </div>
    );
};

export default EmotionAnalyzer;
