// EmotionAnalyzer.js
import React, { useState } from "react";
import { TextField, Button, LinearProgress, Typography } from "@mui/material";
import axios from "axios";

function EmotionAnalyzer() {
    const [text, setText] = useState("");
    const [emotionScore, setEmotionScore] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleTextChange = (e) => {
        setText(e.target.value);
    };

    const handleSubmit = async () => {
        try {
            setLoading(true); //http://emoapp-api.azurewebsites.net/uploadgetresults?input=
            const response = await axios.post("http://localhost:3000/diagnose", {text});
             setEmotionScore(response.score);
        } catch (error) {
            console.error("Error submitting text:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <Typography variant="h5">Hãy mô tả cảm xúc của bạn</Typography>
            <TextField
                multiline
                rows={4}
                variant="outlined"
                fullWidth
                margin="normal"
                onChange={handleTextChange}
                value={text}
            />
            <Button variant="contained" onClick={handleSubmit} disabled={loading}>
                {loading ? "Đang xử lý..." : "Submit"}
            </Button>
            {emotionScore !== null && (
                <div style={{ marginTop: "20px" }}>
                    <Typography variant="h6">Mức độ cảm xúc: {emotionScore}</Typography>
                    <LinearProgress
                        variant="determinate"
                        value={emotionScore * 100}
                        sx={{ marginTop: "10px", height: "10px" }}
                    />
                </div>
            )}
        </div>
    );
};

export default EmotionAnalyzer;
