import 'regenerator-runtime/runtime'
import React, { useState } from "react";
import {TextField, Button, LinearProgress, Typography, Box} from "@mui/material";
import axios from "axios";
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

function EmotionAnalyzer() {
    const [text, setText] = useState("");
    const [emotionScore, setEmotionScore] = useState(null);
    const [emotionLabel, setEmotionLabel] = useState("Tốt");
    const [loading, setLoading] = useState(false);
    const [color, setColor] = useState('blue')

    const handleTextChange = (e) => {
        setText(e.target.value);
    };

    const handleSubmit = async () => {
        try {
            setLoading(true);
            const response = await axios.post("/api/diagnose", {text});
            const {score, label} = response.data.data;
            const isBadState = +label === 0;
            const emotionLabel = isBadState ? "Tệ" : "Tốt"
            setEmotionLabel(emotionLabel)
            setEmotionScore(score);

            if(isBadState) {
                if(score > 0.75) {
                    setColor('#f84a00')
                } else {
                    setColor('#ef9479')
                }
            } else {
                if(score > 0.75) {
                    setColor('#008000')
                } else {
                    setColor('#a3ad09')

                }
            }
        } catch (error) {
            console.error("Không thể phân tích nội dung yêu cầu:", error);
        } finally {
            setLoading(false);
        }
    };


    const { transcript, listening, resetTranscript } = useSpeechRecognition({language: 'vi-VN'});

    React.useEffect(() => {
        setText(transcript);
    }, [transcript]);

    const startListening = function () {
        resetTranscript();
        SpeechRecognition.startListening({continuous: true})
    };


    const [language, setLanguage] = useState('en-US')
    React.useEffect(() => {
        setLanguage(navigator.language)
    }, [])
    const isSupportVNs = language === 'vi-VN';

    return (
        <Box
            display="flex"
            flexDirection="column"
            justifyContent="center"
            alignItems="center"
            sx={{
                width: "80%",
                position: "absolute",
                top: "20%",
                left: "50%",
                transform: "translate(-50%, -50%)",
            }}
        >
            <Typography variant="h5">Hãy mô tả cảm xúc của bạn</Typography>
            <TextField
                multiline
                rows={4}
                variant="outlined"
                fullWidth
                margin="normal"
                onChange={handleTextChange}
                value={text}
                inputProps={{
                    maxLength: 5000,
                }}
            />
            <Box display="flex" justifyContent="center">
                <Button variant="contained" onClick={listening ? SpeechRecognition.stopListening : startListening} disabled={!isSupportVNs} sx={{
                    marginRight: "20px"
                }}>
                    {listening ? 'Dừng nói' : 'Nói để điền nội dung'}
                </Button>
                <Button variant="contained" onClick={handleSubmit} disabled={loading}>
                    {loading ? "Đang xử lý..." : "Phân tích"}
                </Button>
            </Box>

            {emotionScore !== null && (
                <div style={{ marginTop: "20px", width: '100%'}}>
                    <Typography variant="h6" sx={{
                        display: 'flex',  justifyContent:'center', alignItems:'center'
                    }}>Mức độ cảm xúc: <br/> {emotionLabel} - {emotionScore * 100}%</Typography>
                    <LinearProgress
                        variant="determinate"
                        value={emotionScore * 100}
                        sx={{ marginTop: "10px", height: "10px",
                            '& .MuiLinearProgress-bar': {
                                backgroundColor: color
                            }}}
                    />
                </div>
            )}
        </Box>
    );
};

export default EmotionAnalyzer;
