// EmotionAnalyzer.js
import React, { useState } from "react";
import { TextField, Button, LinearProgress, Typography } from "@mui/material";
import axios from "axios";

function EmotionAnalyzer() {
    const [text, setText] = useState("");
    const [emotionScore, setEmotionScore] = useState(null);
    const [emotionLabel, setEmotionLabel] = useState("Tốt");
    const [loading, setLoading] = useState(false);

    const [transcript, setTranscript] = useState('');
    const [isListening, setIsListening] = useState(false);

    const handleTextChange = (e) => {
        setText(e.target.value);
    };

    const handleSubmit = async () => {
        try {
            setLoading(true);
            const response = await axios.post("http://localhost:3000/api/diagnose", {text});
            const {score, label} = response.data.data;
            const emotionLabel = +label === 0 ? "Tệ" : "Tốt"
            setEmotionLabel(emotionLabel)
            setEmotionScore(score);
        } catch (error) {
            console.error("Error submitting text:", error);
        } finally {
            setLoading(false);
        }
    };


    const startSpeechRecognition = async () => {
        const recognition = new window.SpeechRecognition();
        recognition.lang = 'en-US';

        recognition.onresult = (event) => {
            const transcript = event.results[0][0].transcript;
            setTranscript(transcript);
        };

        recognition.onend = () => {
            setIsListening(false);
        };

        setIsListening(true);
        recognition.start();
    };

    const stopSpeechRecognition = () => {
        window.speechSynthesis.cancel(); // Dừng chuyển văn bản thành giọng nói (tùy chọn)
        setIsListening(false);
    };

    const handleButtonClick = () => {
        if (isListening) {
            stopSpeechRecognition();
        } else {
            startSpeechRecognition();
        }
    };


    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <Typography variant="h5">Hãy mô tả cảm xúc của bạn</Typography>
            <TextField
                multiline
                rows={4}
                variant="outlined"
                fullWidth
                margin="normal"
                onChange={handleTextChange}
                value={text}
            />
            <Button variant="contained" onClick={handleButtonClick}>
                {isListening ? 'Stop Listening' : 'Start Listening'}
            </Button>
            <Button variant="contained" onClick={handleSubmit} disabled={loading}>
                {loading ? "Đang xử lý..." : "Submit"}
            </Button>
            {emotionScore !== null && (
                <div style={{ marginTop: "20px" }}>
                    <Typography variant="h6">Mức độ cảm xúc: <br/> {emotionLabel} - {emotionScore * 100}%</Typography>
                    <LinearProgress
                        variant="determinate"
                        value={emotionScore * 100}
                        sx={{ marginTop: "10px", height: "10px" }}
                    />
                </div>
            )}
        </div>
    );
};

export default EmotionAnalyzer;
